    private void GalonesKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_GalonesKeyReleased
        if(Galones.getText().length()==0){
            return;
        }else{
            double entrada = Double.parseDouble(Galones.getText());
            double Litros = (entrada)*(3.7);
            double costo = (Litros)*(21.75);
            
            Recibo.setText(
                    "Gasolineria La Gota \n\n\nRecibo \n"+
                    "Galones de gasolina: " + entrada + "\n"+
                    "Litro de gasolina: "+Litros+"\n"+
                    "Precio de la gasolina: $21.27mxn \n"+
                    "Total a pagar: "+costo
            );
        }
    }//GEN-LAST:event_GalonesKeyReleased

    private void LimpiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_LimpiarActionPerformed
        Galones.setText("");
        Recibo.setText("Gasolineria La Gota");
    }//GEN-LAST:event_LimpiarActionPerformed

    private void GalonesKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_GalonesKeyTyped
        int key = evt.getKeyChar();
        boolean numero = key >= 48 && key <= 57;
        if(!numero){
            evt.consume();
        }
    }//GEN-LAST:event_GalonesKeyTyped


